CustomField.formula:
IF(
   Quote_Type__c == 'Amendment' &&  ISPICKVAL(Amendment_Action__c, 'Added Plant') , 
     (Quote__r.SBQQ__EndDate__c  - Date_of_Amendment__c ) / (Quote__r.SBQQ__EndDate__c  - Quote__r.SBQQ__StartDate__c) , 
   if(
       Quote_Type__c == 'Amendment' &&  ISPICKVAL(Amendment_Action__c, 'Removed Plant') , 
         ( Date_of_Amendment__c  - Quote__r.SBQQ__StartDate__c) / (Quote__r.SBQQ__EndDate__c  - Quote__r.SBQQ__StartDate__c) ,
         1
  )
)
