          BLANKVALUE(
            project_cloud__Load_Override__c * project_cloud__Allocation_Group__r.project_cloud__Hours_Day__c,
            IF(
              AND(project_cloud__Allocation_Group__r.project_cloud__Balanced_Hours_Day__c > 0.0, project_cloud__Allocation_Group__r.project_cloud__Total_Subscriptions__c > 0),
              project_cloud__Allocation_Group__r.project_cloud__Balanced_Hours_Day__c / project_cloud__Allocation_Group__r.project_cloud__Total_Subscriptions__c, 0
            )
          )
        )
      )
