ValidationRule.errorConditionFormula:
(
	ISPICKVAL(project_cloud__Units__c, '') ||
	ISCHANGED(project_cloud__Units__c)
) && (
	/*Special Cases for when the migration helper updates the values*/
	NOT(ISPICKVAL(PRIORVALUE(project_cloud__Units__c), "Fixed Duration")) ||
	NOT(ISPICKVAL(PRIORVALUE(project_cloud__Units__c), "Fixed Work"))
)
